<div id="carousel-{slider-category}" class="carousel slide carousel-fade" data-ride="carousel">

    <ol class="carousel-indicators">{slider-indicators}</ol>

    <div class="carousel-inner" role="listbox">
        {slider-slides}
    </div>

    <a class="carousel-control-prev" href="#carousel-{slider-category}" role="button" data-slide="prev">
                <span class="carousel-control-prev-icon" aria-hidden="true">
                    <svg version="1.1"
                         xmlns="http://www.w3.org/2000/svg"
                         xmlns:xlink="http://www.w3.org/1999/xlink"
                         x="0px" y="0px"
                         viewBox="0 0 4.5 8"
                         style="enable-background:new 0 0 4.5 8;"
                         xml:space="preserve">
                <path d="M4,0L0,4l4,4l0.5-0.5L1,4l3.5-3.5L4,0z"/>
                </svg></span>
        <span class="sr-only">Previous</span>
    </a>
    <a class="carousel-control-next" href="#carousel-{slider-category}" role="button" data-slide="next">
                <span class="carousel-control-next-icon" aria-hidden="true">
                    <svg version="1.1"
                         xmlns="http://www.w3.org/2000/svg"
                         xmlns:xlink="http://www.w3.org/1999/xlink"
                         x="0px" y="0px"
                         viewBox="0 0 4.5 8"
                         style="enable-background:new 0 0 4.5 8;"
                         xml:space="preserve">
                <path d="M0.5,8l4-4l-4-4L0,0.5L3.5,4L0,7.5L0.5,8z"/>
                </svg></span>
        <span class="sr-only">Next</span>
    </a>

</div>
